function plusSlides(e) {
    showSlides(slideIndex += e);
}

function currentSlide(e) {
    showSlides(slideIndex = e);
}

function showSlides(e) {
    var t, n = document.getElementsByClassName("banner-text-box");
    for (e > n.length && (slideIndex = 1), e < 1 && (slideIndex = n.length), t = 0; t < n.length; t++) n[t].style.display = "none";
    n[slideIndex - 1].style.display = "block";
}

function plusText(e) {
    showText(textIndex += e);
}

function currentText(e) {
    showText(textIndex = e);
}

function showText(e) {
    var t, n = document.getElementsByClassName("clients-text-box");
    for (e > n.length && (textIndex = 1), e < 1 && (textIndex = n.length), t = 0; t < n.length; t++) n[t].style.display = "none";
    n[textIndex - 1].style.display = "block";
}

var slideIndex = 1;

showSlides(slideIndex), $(document).ready(function() {
    $(".more").click(function() {
        $(".portfolio-images-more").toggleClass("active");
    });
});

var textIndex = 1;

showText(textIndex);